<?php

if ( ! defined( 'ABSPATH' ) ) exit;

include_once CHURNSOLUTION_DIR . '/classes/class-churnsolution-connection.php';

// Check if there is an existing connection
$connection = Churnsolution_Connection::get_connection();

// Initialize variables for app ID and API key
$appId = sanitize_text_field($_REQUEST['appId']);
$apiKey = sanitize_text_field($_REQUEST['apiKey']);
$fromRedirect = "false";

// Check if URL parameters are present and override existing connection if they exist
if (!empty($appId) || !empty($apiKey)) {
    $fromRedirect = "true";
} elseif (!empty($connection)) {
    // If URL parameters are not present, use the first existing connection
    $appId = $connection->getAppId();
    $apiKey = $connection->getApiKey();
}

if (!empty(sanitize_text_field($_REQUEST['submit'])) && !empty(sanitize_text_field($_REQUEST['gateway']))) {

    $gateway = sanitize_text_field($_REQUEST['gateway']);

    if(strtolower($gateway) != 'stripe'){
        throw new Exception("No gateway other than stripe is allowed", 400);
    }

    $connection_data = array(
        'app_id' => sanitize_text_field($_REQUEST['app_id']),
        'api_key' => sanitize_text_field($_REQUEST['api_key']),
        'submit' => sanitize_text_field($_REQUEST['submit']),
        'gateway' => $gateway
    );

    $state = Churnsolution_Connection::create_or_update($connection_data);

    unset($_REQUEST['app_id']);
    unset($_REQUEST['api_key']);
    unset($_REQUEST['submit']);
    unset($_REQUEST['gateway']);

    $connection = Churnsolution_Connection::get_connection();
    $appId = $connection->getAppId();
    $apiKey = $connection->getApiKey();
} else if (!empty($_REQUEST['delete'])) {
    Churnsolution_Connection::delete();

    unset($_REQUEST['delete']);

    $connection = Churnsolution_Connection::get_connection();
    $appId = $connection->getAppId();
    $apiKey = $connection->getApiKey();
}


